import React, { useState } from 'react';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [graphs, setGraphs] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);
  
    const response = await fetch('http://127.0.0.1:5000/upload', {
        method: 'POST',
        body: formData,
      });
  
    if (response.ok) {
      const graphUrls = await response.json(); // Returns URLs for the graphs
      console.log(graphUrls); // Debugging here
      setGraphs(graphUrls);
    } else {
      console.error("File upload failed");
    }
  };
  
  return (
    
    <div>
      <h2>Upload a CSV File</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload and Generate Graphs</button>

      <div>
        {graphs.map((url, index) => (
          <div key={index}>
            <h3>Graph {index + 1}</h3>
            <img src={`http://127.0.0.1:5000${url}`} alt={`Graph ${index + 1}`} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default FileUpload;
