import React, { useState } from 'react';
import Chatbot from '../../Chatbot';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [graphs, setGraphs] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);
  
    const response = await fetch('http://127.0.0.1:5000/upload', {
        method: 'POST',
        body: formData,
      });
  
    if (response.ok) {
      const graphUrls = await response.json(); // Returns URLs for the graphs
      console.log(graphUrls); // Debugging here
      setGraphs(graphUrls);
    } else {
      console.error("File upload failed");
    }
  };
  
  return (
    
    <div>
      {/* Sidebar */}
      <div className="sidebar" style={{float:"left", marginRight:"00px"}}>
                <div className="icon">
                    <i className="fas fa-headset"></i>
                </div>
                <div className="icon">
                    <i className="fas fa-check"></i>
                </div>
                <div className="icon">
                    <i className="fas fa-users"></i>
                </div>
                <div className="icon">
                    <a href="http://127.0.0.1:5000" target="_blank" rel="noopener noreferrer">
                        <i className="fas fa-comment"></i>
                    </a>
                </div>


                <div className="icon">
                    <i className="fas fa-cog"></i>
                </div>
                <div className="icon">
                    <i className="fas fa-arrow-left"></i>
                    <i className="fas fa-bolt"></i>
                </div>
                <div className="">
                    <Chatbot />
                 
                </div>
            </div>
      <h2>Upload a CSV File</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload and Generate Graphs</button>

      <div>
        {graphs.map((url, index) => (
          <div key={index}>
            <h3 style={{float:"left", marginRight:"500px",marginTop:"0px"}}>Graph {index + 1}</h3>
            <img src={`http://127.0.0.1:5000${url}`} alt={`Graph ${index + 1}`} style={{}} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default FileUpload;
